package plugins

import (
	"fmt"
	"testing"

	"github.com/quintans/gog/config"
)

func TestRecord(t *testing.T) {
	tests := []struct {
		name string
		in   string
		out  string
	}{
		{
			"Record",
			`
package p

// gog:record
type Foo struct {
	name  string
	value int64
}
`,
			fmt.Sprintf(`// Code generated by gog; DO NOT EDIT.
// Version: %s
package p

import "fmt"

// Generated by gog:record

func NewFoo(
	name string,
	value int64,
) Foo {
	f := Foo{
		name:  name,
		value: value,
	}

	return f
}

func (f Foo) Name() string {
	return f.name
}

func (f Foo) Value() int64 {
	return f.value
}

func (f Foo) IsZero() bool {
	return f == Foo{}
}

func (f Foo) String() string {
	return fmt.Sprintf("Foo{name: %%+v, value: %%+v}", f.name, f.value)
}
`, config.Version),
		},
		{
			"Record",
			`
package p

// gog:record
type Foo struct {
	name string
}

func (f Foo) String() string {
	return f.name
}
`,
			fmt.Sprintf(`// Code generated by gog; DO NOT EDIT.
// Version: %s
package p

// Generated by gog:record

func NewFoo(
	name string,
) Foo {
	f := Foo{
		name: name,
	}

	return f
}

func (f Foo) Name() string {
	return f.name
}

func (f Foo) IsZero() bool {
	return f == Foo{}
}
`, config.Version),
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			run(t, tt.in, tt.out)
		})
	}
}
